package main

import (
	"fmt"
)

func isValid(s string) bool {
	var que [65536]byte
	var que_len int = 0
	if (len(s)) <= 1 {
		return false
	}

	for i := 0; i < len(s); i++ {
		switch s[i] {
		case '(', '[', '{':
			que[que_len] = s[i]
			que_len++
		case ')':
			if que_len >= 1 && que[que_len-1] == '(' {
				que_len--
			} else {
				// fmt.Println("ret 2")
				return false
			}
			// golang 默认添加了break
		case ']':
			if que_len >= 1 && que[que_len-1] == '[' {
				que_len--
			} else {
				// fmt.Println("ret 3")
				return false
			}
		case '}':
			if que_len >= 1 && que[que_len-1] == '{' {
				que_len--
			} else {
				// fmt.Println("ret 4")
				return false
			}
		default:
			// fmt.Println("ret 5")
			return false
		}
	}
	// input "(("
	if que_len > 0 {
		return false
	}

	return true
}

